from flask_wtf import FlaskForm
from wtforms import StringField, PasswordField, BooleanField, IntegerField, DecimalField, TextAreaField
from wtforms.validators import DataRequired, Email, Length, NumberRange, ValidationError
from models import Produto

class LoginForm(FlaskForm):
    email = StringField('Email', validators=[
        DataRequired(message='Email Ã© obrigatÃ³rio'),
        Email(message='Email invÃ¡lido')
    ])
    password = PasswordField('Senha', validators=[
        DataRequired(message='Senha Ã© obrigatÃ³ria')
    ])
    remember_me = BooleanField('Lembrar de mim')

class ProdutoForm(FlaskForm):
    nome = StringField('Nome', validators=[
        DataRequired(message='Nome Ã© obrigatÃ³rio'),
        Length(min=2, max=100, message='Nome deve ter entre 2 e 100 caracteres')
    ])
    sku = StringField('SKU', validators=[
        DataRequired(message='SKU Ã© obrigatÃ³rio'),
        Length(min=2, max=50, message='SKU deve ter entre 2 e 50 caracteres')
    ])
    descricao = TextAreaField('DescriÃ§Ã£o', validators=[
        Length(max=500, message='DescriÃ§Ã£o deve ter no mÃ¡ximo 500 caracteres')
    ])
    quantidade = IntegerField('Quantidade', validators=[
        DataRequired(message='Quantidade Ã© obrigatÃ³ria'),
        NumberRange(min=0, message='Quantidade deve ser maior ou igual a 0')
    ])
    preco_venda = DecimalField('PreÃ§o de Venda', validators=[
        DataRequired(message='PreÃ§o de venda Ã© obrigatÃ³rio'),
        NumberRange(min=0.01, message='PreÃ§o deve ser maior que 0')
    ], places=2)

    def __init__(self, produto_id=None, *args, **kwargs):
        super(ProdutoForm, self).__init__(*args, **kwargs)
        self.produto_id = produto_id

    def validate_sku(self, sku):
        produto = Produto.query.filter_by(sku=sku.data).first()
        if produto:
            if self.produto_id is None or produto.id != self.produto_id:
                raise ValidationError('SKU jÃ¡ existe. Use um SKU Ãºnico.')

